tinymce.PluginManager.add('photo', function(editor, url) {
    editor.addButton('photo', {
        title: 'Tambah Foto',
        icon: 'image',
        onclick: function() {
            $('#modal-general-title').html('Photo');
            $('#modal-general-iframe').attr('src', base_url + 'photo');
            MicroModal.show('modal-general');
        }
    });
});