tinymce.PluginManager.add('video', function(editor, url) {
    editor.addButton('video', {
        title: 'Insert video',
        icon: 'media',
        onclick: function() {
            var title = 'Add Video';
            var src = base_url + 'video/index?modals=true'
            $('#general-modal-title').html(title);
            $('#general-modal-iframe').attr('src',src);
            $('#general-modal').modal('show');
        }
    });
});
