tinymce.PluginManager.add('video', function(editor, url) {
    editor.addButton('video', {
        title: 'Tambah Video',
        icon: 'media',
        onclick: function() {
            $('#modal-general-title').html('video');
            $('#modal-general-iframe').attr('src', base_url + 'video');
            MicroModal.show('modal-general');
        }
    });
});
